                    <circle cx={8} cy={8} r={8} />
                  </svg></div>
                <div className="col-8">
                  <p style={{marginBottom: 0, fontSize: 20}}>{task.task}</p>
                </div>
                <div onClick={() => editTask(task)} className="col-auto d-flex justify-content-center align-items-center"><svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" fill="currentColor" viewBox="0 0 16 16" className="bi bi-pencil-fill" style={{color: 'rgb(2,244,157)', cursor: "pointer"}}>
