                    <circle cx={8} cy={8} r={8} />
                  </svg></div>
                <div className="col-8">
                  <p style={{marginBottom: 0, color: 'rgba(33,37,41,0.45)', textDecoration: 'line-through', fontSize: 12}}>Paragraph</p>
