import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const response = await axios.get('http://localhost:5000/api/todos');
    setTasks(response.data);
  };

  const addTask = async () => {
    if (newTask.trim() === '') return;
    const response = await axios.post('http://localhost:5000/api/todos', { task: newTask });
    setTasks([...tasks, response.data]);
    setNewTask('');
  };

  const updateTask = async (id) => {
    if (newTask.trim() === '') return;
    const response = await axios.put(`http://localhost:5000/api/todos/${id}`, { task: newTask });
    setTasks(tasks.map(task => (task._id === id ? response.data : task)));
    setNewTask('');
    setEditingTask(null);
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`);
    setTasks(tasks.filter(task => task._id !== id));
  };

  const editTask = (task) => {
    setNewTask(task.task);
    setEditingTask(task._id);
  };

  return (
    <>
   <header className="d-flex justify-content-center align-items-center" style={{height: '100vh'}}>
  <div className="container d-flex justify-content-center align-items-center" style={{height: '100%', width: '100%'}}>
    <div className="row d-flex justify-content-center align-items-center" style={{height: '100%', width: '100%'}}>
      <div className="col d-flex justify-content-center align-items-center" style={{height: '100%', width: '100%'}}>
        <div className="d-flex flex-column justify-content-around align-items-center" style={{height: '80%', background: '#ffffff', width: '50%', borderRadius: 10, boxShadow: '5px 6px 16px 9px rgba(33,37,41,0.2)', minWidth: 300, padding: 20}}>
          <div className="row" style={{width: '100%'}}>
            <div className="col">
              <h1 className="text-center">To-Do List</h1>
            </div>
          </div>
          <div className="row d-flex justify-content-center align-items-start" style={{width: '100%', marginBottom: 20, height: '60%'}}>
            <div className="col d-flex flex-column justify-content-start align-items-center" style={{width: '95%', height: '100%', overflowY: 'scroll'}}>
              <div className="row d-flex justify-content-start" style={{width: '100%', marginBottom: 5, marginTop: 5, paddingTop: 20, paddingBottom: 20, cursor: 'pointer'}}>
                <div className="col-2 d-flex justify-content-center align-items-center"><svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" fill="currentColor" viewBox="0 0 16 16" className="bi bi-circle-fill" style={{fontSize: 20, color: 'rgba(33,37,41,0.13)'}}>
                    <circle cx={8} cy={8} r={8} />
                  </svg></div>
                <div className="col-8">
                  <p style={{marginBottom: 0, fontSize: 20}}>Paragraph</p>
                </div>
                <div className="col-auto d-flex justify-content-center align-items-center"><svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" fill="currentColor" viewBox="0 0 16 16" className="bi bi-pencil-fill" style={{color: 'rgb(2,244,157)'}}>
