import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const response = await axios.get("http://localhost:5000/api/todos");
    setTasks(response.data);
  };

  const addTask = async () => {
    if (newTask.trim() === "") return;
    const response = await axios.post("http://localhost:5000/api/todos", {
      task: newTask,
    });
    setTasks([...tasks, response.data]);
    setNewTask("");
  };

  const updateTask = async (id) => {
    if (newTask.trim() === "") return;
    const response = await axios.put(`http://localhost:5000/api/todos/${id}`, {
      task: newTask,
    });
    setTasks(tasks.map((task) => (task._id === id ? response.data : task)));
    setNewTask("");
    setEditingTask(null);
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`);
    setTasks(tasks.filter((task) => task._id !== id));
  };

  const editTask = (task) => {
    setNewTask(task.task);
    setEditingTask(task._id);
  };

  const toggleComplete = async (id, completed) => {
    const response = await axios.put(`http://localhost:5000/api/todos/${id}`, { completed: !completed });
    setTasks(tasks.map(task => (task._id === id ? response.data : task)));
  };

  return (
    <>
      <header
        className="d-flex justify-content-center align-items-center"
        style={{ height: "100vh" }}
      >
        <div
          className="container d-flex justify-content-center align-items-center"
          style={{ height: "100%", width: "100%" }}
        >
          <div
            className="row d-flex justify-content-center align-items-center"
            style={{ height: "100%", width: "100%" }}
          >
            <div
              className="col d-flex justify-content-center align-items-center"
              style={{ height: "100%", width: "100%" }}
            >
              <div
                className="d-flex flex-column justify-content-around align-items-center"
                style={{
                  height: "80%",
                  background: "#ffffff",
                  width: "50%",
                  borderRadius: 10,
                  boxShadow: "5px 6px 16px 9px rgba(33,37,41,0.2)",
                  minWidth: 300,
                  padding: 20,
                }}
              >
                <div className="row" style={{ width: "100%" }}>
                  <div className="col">
                    <h1 className="text-center">To-Do List</h1>
                  </div>
                </div>
                <div
                  className="row d-flex justify-content-center align-items-start"
                  style={{ width: "100%", marginBottom: 20, height: "60%" }}
                >
                  <div
                    className="col d-flex flex-column justify-content-start align-items-center"
                    style={{
                      width: "95%",
                      height: "100%",
                      overflowY: "scroll",
                    }}
                  >
                    {tasks.map((task) => (
                      <>
                      {task.completed === 'completed' ? (
                        <div
                          className="row d-flex justify-content-start"
                          style={{
                            width: "100%",
                            marginBottom: 5,
                            marginTop: 5,
                            paddingTop: 20,
                            paddingBottom: 20,
                            cursor: "pointer",
                          }}
                        >
                          <div className="col-2 d-flex justify-content-center align-items-center">
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="1em"
                              height="1em"
                              fill="currentColor"
                              viewBox="0 0 16 16"
                              className="bi bi-circle-fill"
                              style={{
                                fontSize: 20,
                                color: "rgba(33,37,41,0.13)",
                              }}
                            >
                              <circle cx={8} cy={8} r={8} />
                            </svg>
                          </div>
                          <div className="col-8">
                            <p style={{ marginBottom: 0, fontSize: 20 }}>
                              {task.task}
                            </p>
                          </div>
                          <div
                            onClick={() => editTask(task)}
                            className="col-auto d-flex justify-content-center align-items-center"
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="1em"
                              height="1em"
                              fill="currentColor"
                              viewBox="0 0 16 16"
                              className="bi bi-pencil-fill"
                              style={{
                                color: "rgb(2,244,157)",
                                cursor: "pointer",
                              }}
                            >
                              <path d="M12.854.146a.5.5 0 0 0-.707 0L10.5 1.793 14.207 5.5l1.647-1.646a.5.5 0 0 0 0-.708l-3-3zm.646 6.061L9.793 2.5 3.293 9H3.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.207l6.5-6.5zm-7.468 7.468A.5.5 0 0 1 6 13.5V13h-.5a.5.5 0 0 1-.5-.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.5-.5V10h-.5a.499.499 0 0 1-.175-.032l-.179.178a.5.5 0 0 0-.11.168l-2 5a.5.5 0 0 0 .65.65l5-2a.5.5 0 0 0 .168-.11l.178-.178z" />
                            </svg>
                          </div>
                          <div
                            onClick={() => deleteTask(task._id)}
                            className="col-auto d-flex justify-content-center align-items-center"
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="1em"
                              height="1em"
                              fill="currentColor"
                              viewBox="0 0 16 16"
                              className="bi bi-trash-fill"
                              style={{
                                color: "rgb(224,58,5)",
                                cursor: "pointer",
                              }}
                            >
                              <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5M8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5m3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0" />
                            </svg>
                          </div>
                        </div>
                      ):(
                        {/* <div className="row d-flex justify-content-start" style={{width: '100%', marginBottom: 5, marginTop: 5, paddingTop: 20, paddingBottom: 20, cursor: 'pointer'}}>
                <div className="col-2 d-flex justify-content-center align-items-center"><svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" fill="currentColor" viewBox="0 0 16 16" className="bi bi-circle-fill" style={{fontSize: 8, color: 'rgb(0,255,163)'}}>
                    <circle cx={8} cy={8} r={8} />
                  </svg></div>
                <div className="col-8">
                  <p style={{marginBottom: 0, color: 'rgba(33,37,41,0.45)', textDecoration: 'line-through', fontSize: 12}}>Paragraph</p>
